SELECT
 --产考上面自动生成的代码
FROM user_table
WHERE username=#{username}
and password=#{password}

-- 多个参数必须用map
SELECT COUNT (1) FROM user_table WHERE
username=#{username} AND question=#{question} AND answer =#{answer}

SELECT COUNT (1) FROM user_table WHERE
id = #{userid} AND
password = #{password}

SELECT COUNT (1) user_table WHERE
email=#{email}
AND
id!=#{userid}
